import React, { useState } from "react"
import "bootstrap/dist/css/bootstrap.min.css"
import { Col, Button, Row, Container, Card, Form } from "react-bootstrap";


function UrlArea() {

    const [url, setUrl] = useState('');
    const [barcodeImage, setBarcodeImage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();


      };




    return (
        <Container>
        <Row className="m-5 d-flex justify-content-center align-items-center">
          <Col md={8} lg={6} xs={12}>
            <div className="border border-3 border-primary"></div>
            <Card className="shadow">
              <Card.Body>
                <div className="mb-3 mt-4">
                  <h2 className="fw-bold mb-2 text-center text-uppercase">URL SH.RTNE. </h2>

                  <Form onSubmit={handleSubmit} className="mb-3">
                  <Form.Group className="mb-3">
                  <Form.Control
                  type="text" 
                  placeholder="Paste a link to shorten"
                  value={url}
                  onChange={(e) => setUrl(e.target.value)} />
                  </Form.Group>

                    <div className="d-grid justify-content-center align-items-center" style={{ height: "100vh" }}>
                      <Button variant="primary" type="submit" >
                        Go!
                      </Button>
                    </div>

                  </Form>
                 <div className="rounded mx-auto d-flex flex-column align-items-center justify-content-center">
                      {barcodeImage && <img src={barcodeImage} alt="Barcode" />}

                 </div>

                </div>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    )

}


export default UrlArea;
